import{_ as t}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as o,f as n}from"./app.1761c271.js";const s={},r=n('<h2 id="chain-tools" tabindex="-1"><a class="header-anchor" href="#chain-tools" aria-hidden="true">#</a> Chain Tools</h2><p><img src="https://www.joleanes.com/uploads/3/0/6/8/30680865/3439183_orig.jpg" alt=""></p><p>\u4F7F\u7528\u6B64\u5DE5\u5177\uFF0C\u60A8\u53EF\u4EE5\u521B\u5EFA\u8FDE\u63A5\u5230\u6837\u6761\u7EBF\u7684\u9AA8\u9ABC\u94FE\uFF0C\u8FD9\u5BF9\u4E8E\u5236\u4F5C\u5F2F\u66F2\u7684\u56DB\u80A2\u6216\u7528\u4E8E\u5C3E\u5DF4\u6216\u810A\u690E\u7684 IK \u94FE\u5F88\u6709\u7528\u3002\u5B83\u4F7F\u7528\u4F4D\u7F6E\u548C\u89C2\u5BDF\u7EA6\u675F\u6216\u4ECE\u9AA8\u9ABC\u5230\u5177\u6709\u8DEF\u5F84\u7EA6\u675F\u7684\u52A9\u624B\u7684\u811A\u672C\u63A7\u5236\u5668\uFF0C\u4EE5\u6837\u6761\u7EBF\u4E3A\u76EE\u6807\uFF0C\u53EF\u4EE5\u5E94\u7528\u504F\u79FB\u503C\u3002\u5B83\u53EF\u4EE5\u4F7F\u7528\u6837\u6761 ik \u6C42\u89E3\u5668\u521B\u5EFA\u4E00\u4E2A\u989D\u5916\u7684\u94FE\u6765\u63A7\u5236\u62C9\u4F38\u3002\u4E5F\u53EF\u4EE5\u4F7F\u7528\u6837\u6761\u7EBF IK \u63A7\u4EF6\u6216\u8499\u76AE\u4FEE\u6539\u5668\u5C06\u6837\u6761\u7EBF\u7EA6\u675F\u5230\u8F85\u52A9\u5BF9\u8C61\u3002</p><h2 id="\u8BBE\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u7F6E" aria-hidden="true">#</a> \u8BBE\u7F6E</h2><p><strong>\u9AA8\u9ABC\u8BBE\u7F6E</strong>\uFF1A\u53EF\u4EE5\u5728\u6B64\u5904\u7F16\u8F91\u4E00\u4E9B\u9AA8\u9ABC\u5C5E\u6027\u3002</p><p>**\u9AA8\u9ABC\uFF1A**\u8981\u5728\u94FE\u4E2D\u521B\u5EFA\u7684\u9AA8\u9ABC\u6570\u91CF\u3002 **\u5BBD\u5EA6\uFF1A**\u94FE\u9AA8\u7684\u5BBD\u5EA6\u3002 **\u9525\u5EA6\uFF1A**\u94FE\u9AA8\u7684\u9525\u5EA6\u3002 **\u6324\u538B\uFF1A**\u8BBE\u7F6E\u62C9\u4F38\u9009\u9879\u4EE5\u6324\u538B\u94FE\u9AA8\u3002</p><p>**LookAt\uFF1A**\u8FD9\u662F\u5B9A\u4F4D\u9AA8\u9ABC\u4EE5\u67E5\u770B\u5176\u76EE\u6807\uFF08\u9644\u52A0\u5230\u6837\u6761\u7EBF\u7684\u8F85\u52A9\u5BF9\u8C61\uFF09\u7684\u65B9\u5F0F\u3002</p><p>**LookAt Constraint\uFF1A**\u5982\u679C\u8BBE\u7F6E\u4E86\u8FD9\u4E2A\u9009\u9879\uFF0C\u9AA8\u9ABC\u5C06\u5206\u914D\u6807\u51C6\u7684lookat\u7EA6\u675F\uFF0C\u5E76\u5C06\u63A7\u5236\u70B9\u52A9\u624B\u4F5C\u4E3A\u76EE\u6807 **Quaternion Aim\uFF1A**\u5982\u679C\u8BBE\u7F6E\u4E86\u8FD9\u4E2A\u9009\u9879\uFF0C\u9AA8\u9ABC\u5C06\u6709\u65CB\u8F6C\u811A\u672C\u800C\u4E0D\u662Flookat\u7EA6\u675F\uFF0C\u4F18\u70B9\u662F\u811A\u672C\u63A7\u5236\u5668\u6709\u4E00\u79CD\u901A\u8FC7\u6784\u5EFA\u56DB\u5143\u6570\u6765\u67E5\u627E\u65CB\u8F6C\u503C\u7684\u67E5\u770B\u7B97\u6CD5\uFF0C\u8FD9\u53EF\u4EE5\u9632\u6B62\u4E0D\u9700\u8981\u7684\u7FFB\u8F6C\uFF0C\u9664\u975E\u9AA8\u9ABC\u4E0E\u5176\u7236\u7EA7\u91CD\u53E0\uFF08x \u8F74\u6B63\u5728\u5BFB\u627E\u76F8\u53CD\u7684\u65B9\u5411\uFF09\u3002</p><p>**\u94FE\u70B9\u8BBE\u7F6E\uFF1A**\u94FE\u70B9\u662F\u5C06\u901A\u8FC7\u8DEF\u5F84\u7EA6\u675F\u9644\u52A0\u5230\u6837\u6761\u7EBF\u7684\u52A9\u624B\uFF0C\u5728\u8FD9\u91CC\u60A8\u53EF\u4EE5\u8BBE\u7F6E\u5B83\u4EEC\u7684\u884C\u4E3A</p><p>**\u6EDA\u52A8\u5E73\u5747\u503C\uFF1A**\u94FE\u70B9\u5C06\u5177\u6709\u76F8\u5BF9\u4E8E\u63A7\u5236\u70B9\u6216\u5FAE\u8C03\u5668\u7684\u65B9\u5411\u5E73\u5747\u503C\uFF08x \u65CB\u8F6C\uFF09\uFF0C\u5177\u4F53\u53D6\u51B3\u4E8E\u6240\u9009\u9009\u9879\u3002</p><p><strong>\u5E73\u5747\u5230\uFF1A</strong></p><p><strong>\u7B2C\u4E00\u4E2A\u548C\u6700\u540E\u4E00\u4E2A\u63A7\u4EF6\uFF1A</strong> \u4E0E\u6837\u6761\u66F2\u7EBF\u7684\u7B2C\u4E00\u4E2A\u548C\u6700\u540E\u4E00\u4E2A\u9876\u70B9\u7B49\u6548\u7684\u63A7\u5236\u70B9\u52A9\u624B\u5C06\u63A7\u5236\u6EDA\u52A8\u5E73\u5747\u503C\u3002\uFF08\u63A7\u5236\u70B9\u662F\u63A7\u5236\u6837\u6761\u9876\u70B9\u4F4D\u7F6E\u7684\u52A9\u624B\uFF09\u3002 **\u6240\u6709\u63A7\u5236\uFF1A**\u5C06\u8003\u8651\u6240\u6709\u63A7\u5236\u70B9\u6765\u8BA1\u7B97\u6EDA\u52A8\u5E73\u5747\u503C\u3002 **\u5FAE\u8C03\u5668\uFF1A**\u6EDA\u52A8\u5E73\u5747\u503C\u5C06\u7531\u5B58\u50A8\u5728\u63A7\u5236\u70B9\u6301\u6709\u7684\u5C5E\u6027\u4FEE\u6539\u5668\u4E2D\u7684\u5FAE\u8C03\u5668\u63A7\u5236\u3002</p><p><strong>\u5E73\u5747\u7C7B\u578B\uFF1A</strong></p><p>**Quat \u7EA6\u675F\uFF1A**\u6EDA\u52A8\u5E73\u5747\u503C\u5C06\u4F7F\u7528 quat \u8BA1\u7B97\u6765\u8BA1\u7B97\u3002\u5982\u679C\u63A7\u5236\u70B9\u5C06\u6301\u6709\u4EFB\u4F55\u4E0D\u540C\u4E8E\u6B27\u62C9\u89D2\u7684\u7EA6\u675F\u6216\u63A7\u5236\u5668\uFF0C\u8FD9\u5C06\u5F88\u6709\u7528\u3002 **\u6B27\u62C9\u89D2\uFF1A**\u6EDA\u52A8\u5E73\u5747\u503C\u5C06\u4F7F\u7528\u63A7\u5236\u70B9\u65CB\u8F6C\u7684 x \u8F68\u8FF9\u8BA1\u7B97\u3002\u5C06\u94FE\u6761\u626D\u8F6C\u8D85\u8FC7 180 \u5EA6\u800C\u4E0D\u7FFB\u8F6C\u5F88\u6709\u7528\uFF0C\u7F3A\u70B9\u662F\u6EDA\u52A8\u5E73\u5747\u503C\u53D6\u81EA\u63A7\u5236\u70B9\u7684\u672C\u5730\u6B27\u62C9\u63A7\u5236\u5668\uFF0C\u8FD9\u610F\u5473\u7740\u60A8\u4E0D\u80FD\u4E3A\u5B83\u4EEC\u5206\u914D\u4EFB\u4F55\u7EA6\u675F\uFF0C\u6216\u8005\u4E0E\u6B27\u62C9\u4E0D\u540C\u7684\u65CB\u8F6C\u63A7\u5236\u5668\u89D2\u5EA6\u3002</p><p><strong>\u64CD\u7EB5\u63A7\u5236\u5668\uFF1A</strong> \u5982\u679C\u9009\u4E2D\u6B64\u9009\u9879\uFF0C\u5219\u4F1A\u5C06\u5177\u6709\u6D3B\u52A8\u53EF\u7F16\u8F91\u63A7\u5236\u5668\u7684\u5217\u8868\u63A7\u5236\u5668\u5206\u914D\u7ED9\u94FE\u70B9\uFF0C\u4EE5\u4FBF\u72EC\u7ACB\u7F16\u8F91\u5B83\u4EEC\u7684\u53D8\u6362\uFF0C\u94FE\u70B9\u5C06\u540C\u65F6\u5177\u6709\u4F4D\u7F6E\u3001\u8DEF\u5F84\u7EA6\u675F\u548C\u6D3B\u52A8\u63A7\u5236\u5668\u3002</p><p><strong>Extra IK Solver Constraint\uFF1A</strong> \u5982\u679C\u6B64\u9009\u9879\u5904\u4E8E\u6D3B\u52A8\u72B6\u6001\uFF0C\u5219\u53EF\u4EE5\u63A7\u5236\u94FE\u7684\u62C9\u4F38\uFF0C\u5C06\u521B\u5EFA\u4E00\u4E2A\u5FAE\u8C03\u5668\u5E76\u5C06\u5176\u5206\u914D\u7ED9\u63A7\u5236\u70B9\u3002</p><p>**\u6837\u6761\u63A7\u5236\u8BBE\u7F6E\uFF1A**\u6837\u6761\u9876\u70B9\u7684\u63A7\u5236\u65B9\u5F0F\u7531\u8FD9\u4E9B\u5C5E\u6027\u5B9A\u4E49</p><p>**\u6837\u6761\u7EBF IK \u63A7\u5236\uFF1A**\u6837\u6761\u7EBF\u9876\u70B9\u4F4D\u7F6E\u7531\u4F7F\u7528\u6837\u6761\u7EBF IK \u63A7\u5236\u4FEE\u6539\u5668\u7684\u63A7\u5236\u70B9\u63A7\u5236\u3002\u8FD9\u79CD\u65B9\u6CD5\u7684\u7F3A\u70B9\u662F\u60A8\u65E0\u6CD5\u63A7\u5236\u5207\u7EBF\u624B\u67C4\u7684\u4F4D\u7F6E\u3002\u552F\u4E00\u7684\u65B9\u6CD5\u662F\u4F7F\u7528\u63A7\u5236\u70B9\u65CB\u8F6C\u3002 **\u8499\u76AE\uFF1A**\u6837\u6761\u7EBF\u9876\u70B9\u4F4D\u7F6E\u7531\u4F7F\u7528\u8499\u76AE\u4FEE\u6539\u5668\u7684\u63A7\u5236\u70B9\u63A7\u5236\u3002\u8FD9\u79CD\u65B9\u6CD5\u7684\u4F18\u70B9\u662F\u60A8\u53EF\u4EE5\u4F7F\u7528\u52A9\u624B\u63A7\u5236\u5207\u7EBF\u624B\u67C4\u7684\u4F4D\u7F6E\u3002 **\u5207\u7EBF\uFF1A**\u5982\u679C\u6B64\u9009\u9879\u5904\u4E8E\u6D3B\u52A8\u72B6\u6001\uFF0C\u5C06\u521B\u5EFA\u52A9\u624B\u6765\u63A7\u5236\u6837\u6761\u7EBF\u9876\u70B9\u7684\u5207\u7EBF\u624B\u67C4\u3002</p><h2 id="\u539F\u6587" tabindex="-1"><a class="header-anchor" href="#\u539F\u6587" aria-hidden="true">#</a> \u539F\u6587</h2><p>Description:With this tool you can create a chain of bones attached to a spline, useful to make bendy limbs or IK chains for tails or spines. It uses position and lookat constraints or scripted controllers from the bones to helpers which have path constraints with the spline as target, offset values can be aplied. It can create an extra chain with a spline ik solver to control stretching. The spline can also be constrained to helper objects using the Spline IK Control or the skin Modifier.</p><h2 id="settings" tabindex="-1"><a class="header-anchor" href="#settings" aria-hidden="true">#</a> Settings</h2><p><strong>Bones Settings</strong>: Some of the bones properties can be edited here.</p><p><strong>Bones:</strong> The number of bones to create in the chain. <strong>Width:</strong> The width of the chain bones. <strong>Taper:</strong> The Taper of the chain bones. <strong>Squash:</strong> Set the stretch option to squash in the the chain bones.</p><p><strong>LookAt:</strong> This is the way that the bones will be orientated to look their targets (the helpers attached to the spline).</p><p><strong>LookAt Constraint:</strong> If this option is set the bones will have standard lookat constraints assigned having the control point helpers as targets <strong>Quaternion Aim:</strong> Is this option is set the bones will have rotation script instead of lookat constraint, the advantage is that the script controllers have a lookat algorithm that finds the rotation value by building a quaternion, this prevent undesirable flips, unless the bone overlaps its parent (the x axis are looking to opposite directions).</p><p><strong>Chain Points Settings:</strong> The chain points are the helpers that are going to be attached to the spline via path constraint, here you can setup their behavior</p><p><strong>Roll Average:</strong> The chain points will have an orientation average (x-rotation) regards to control points or a spinner depending on the chosen option.</p><p><strong>Average To:</strong></p><p><strong>First and Last Controls:</strong> The Control Point helpers equivalent to the first and last vertex of the spline will control the Roll average.(the Control points are helpers that control the spline vertices position). <strong>All Controls:</strong> All the Control Points will be considered to calculate the roll average. <strong>Spinner:</strong> The Roll average will be controlled by a spinner stored in an attribute modifier held by the control points.</p><p><strong>Average Type:</strong></p><p><strong>Quat Constraint:</strong> The roll average will be calculated using quat calculation. it useful if the control points will held any constraint or controller different than an Euler Angles. <strong>Euler Angles:</strong> The roll average will be calculated using the x track of the Control Points rotation. It is useful to twist the chain beyond 180 degrees without flipping, the disadvantage is that the roll average are taken from the local euler controller of the control points, that means that you can&#39;t assign them any constrain, or rotation controller different than euler angles.</p><p><strong>Manipulation Controllers:</strong> If this option is checked a list controller with active editable controllers will be assigned to the chain points in order to edit their transform independently, the chain points will have both position, the path constraint and the active controller.</p><p><strong>Extra IK Solver Constraint:</strong> The stretch of the chain can be controlled if this option is active, a spinner will be created and assigned to the control points.</p><p><strong>Spline Control Settings:</strong> The way that the spline vertices are controlled is defined by these properties</p><p><strong>Spline IK Control:</strong> The spline vertices position are controlled by the Control Points using a Spline IK control modifier. the disadvantage of this method is that you cannot control the tangents handles position. The only way is using the control Points rotation. <strong>Skin:</strong> The spline vertices position are controlled by the Control Points using a Skin modifier. the advantage of this method is that you can control the tangents handles position using helpers. <strong>Tangents:</strong> If this option is active Helpers will be created to control the tangent handles of the spline vertices.</p><blockquote><p>https://www.joleanes.com/chain-tools.html</p></blockquote>',36),i=[r];function a(l,h){return e(),o("div",null,i)}const g=t(s,[["render",a],["__file","ChainsTools.html.vue"]]);export{g as default};
